import React, { useState } from "react";
import "./index.css";
import Todo_list from "./Todo_List";

const App = () => {
  var valo;
  const [arr, updArr] = useState([]);
  const [val, updVal] = useState('');
  const thisVal = (obj) => {
    updVal(obj.target.value);
  }
  const listMake = () => {
    updArr((prearr) => {
      return (
        [...prearr, val]
      )
    });
    updVal('');
  };
  const dleteme = (index) => {
    updArr((prearr) => {
      return (prearr.filter((va, i) => {
        return (i !== index)
      }));
    });
  };
  return (
    <>
      <div>
        <div className="center">
          <div className="cen">
            <h1 className="back">ToDo List ♥</h1>
            <span>
              <input value={v} type="text" placeholder="Enter value" onChange={thisVal} />
              <button onClick={listMake} >+</button>
            </span>
            <ol>
              {arr.map((val, index) => {
                return (<li><a onClick={() => { dleteme(index) }} className="size" href="#">✔</a>{val}</li>)
              })}
            </ol> </div>
        </div>


      </div>
    </>
  )
}
