import React, { useState } from "react";
import "./index.css";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = (prop) => {
const [val, upVal]=useState({
    title:'',
    content:'',
})
const inpo=(obj)=>{
  const value=obj.target.value;
  const name=obj.target.name;
    upVal((preVal)=>{
        return(
            {
               ...preVal,[name]:value,
            }
        )
    })
    ual(()=>{
      return ( {title:'',
        content:'',})
    })
};
const genrate=()=>{
  prop.passNote(val);
}


    return (
        <>
            <div class="mx-auto text-center mt-3" style={{ width: '200px' }}>
                <form className="text-center ">
                    <div class="card shadow-lg " style={{ width: "23rem" }}>

                        <input value={val.title} name="title"  onChange={inpo} type="text"  placeholder="Title                                 ðŸ“·  ðŸ“ ðŸ’¡" />
                       
                        <div class="card-body">
                           
                            <textarea value={val.content} name='content' onChange={inpo} className="w-100 m-0 h-100" placeholder="Enter Text..." rows="" column="" />
                           
                            <Button onClick={genrate}  className="bton">
                                <AddIcon  />
                            </Button>
                        </div>
                    </div>
                </form>
            </div>


        </>
    )
}
export default CreateNote;

