import React, { useState } from "react"
import "./index.css"

const App=()=>{
 var values;
  var [listval, listUpval]=useState("");
  var thisVal=(obj)=>{
    values=obj.target.value;
  }
  const addToList=()=>{
    return(listUpval(values));
  }
  var remove= ()=>{
    document.getElementById('ll').out=" ";
  }
  return(
    <>
    <div className="center">
      <div className="cen">
       <h1 className="back">My Todo Listy</h1>
       <span>
       <input type="text" placeholder="Enter list of Tasks" onChange={thisVal}/>
       <button onClick={addToList}>+</button>
       </span>
       <ol>
         <li id="ll"><a href="#" onClick={remove}>+</a>{listval}</li>
       </ol>
      </div>
    </div>
    </>
  )
}
export default App;
