import React, { useState } from "react";
import "./index.css";

const App = () => {
  let [nam, nameChange]=useState({
    fname:'',
    lname:'',
    email:'',
  })
  let thisPut=(obj)=>{
   const value=obj.target.value;
   const name=obj.target.name;

    nameChange((pre)=>{
      if(name==='fname'){
       return( {
          fname:value,
          lname:pre.lname,
          email:pre.email,
        })
      }else if(name==='lname'){
        return( {
           fname:pre.fname,
           lname:value,
           email:pre.email,
         })}
         
    })
  }
  let sent = (event) => {
    event.preventDefault();
  }
  return (
    <>
      <form onSubmit={sent}>
        <div className="center">
          <div className="cen">
            <h1>Hello</h1>
            <p style={{ color: 'black' }}></p>
            <input type="text" name='fname' onChange={thisPut}/>
            <input type="text" name='lname' onChange={thisPut}/>
            <input type="text" name='email' onChange={thisPut}/>
            <button type='submit'>Submit</button>
          </div>
        </div>
      </form>
    </>
  )
}
export default App;
























