import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import "./index.css";

const App = () => {
  const [val, updVal]=useState(0)
  const increment=()=>{
    return updVal(val+1);
  };
  const decrement=()=>{
    if(val>0){
      return updVal(val-1);
    }else{
      alert('You reached on zero');
    }
   
  }

  return (
    <>
      <div>
        <div className="center">
          <div className="cen">
            <h1 className="back">{val}</h1>
            <span>
            <button  onClick={increment} >Add</button>
              <button className="button1" onClick={decrement}>Del</button>
            </span>
            </div>
        </div>


      </div>
    </>
  )
}
