import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import Tooltip from '@material-ui/core/Tooltip';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';
import "./index.css";

const App = () => {
  const [val, updVal] = useState(0)
  const increment = () => {
    return updVal(val + 1);
  };
  const decrement = () => {
    if (val > 0) {
      return updVal(val - 1);
    } else {
      alert('You reached on zero');
    }

  }

  return (
    <>
      <div>
        <div className="center">
          <div className="cen">
          
            <h1 className="back">{val}</h1>
            <span>
              <Tooltip title="Add">
                <Button className='bt1' onClick={increment} ><AddIcon /></Button>
              </Tooltip>
              <Tooltip title="Delete">
                <Button className="bt2" onClick={decrement}><DeleteIcon /></Button>
              </Tooltip>
            </span>
          </div>
        </div>


      </div>
    </>
  )
}
