import React, { useState } from "react";
import "./index.css";

const App = () => {
  const [val, incrval]=useState(0)
  const increment=()=>{
    return incrval(0+1);
  };
  const decrement=()=>{
    return decrval();
  }

  return (
    <>
      <div>
        <div className="center">
          <div className="cen">
            <h1 className="back">{val}</h1>
            <span>
            <button onClick={increment} >Increment</button>
              <button  onClick={decrement}>Decrement</button>
            </span>
            </div>
        </div>


      </div>
    </>
  )
}
