import React from "react";
import { useParams } from "react-router-dom";
import { useLocation } from "react-router-dom/cjs/react-router-dom.min";

const About=(prop)=>{
  const{nam, nam2}=useParams();
  const loc=useLocation();
  console.log(loc)
  return(
    <>
    <h1>I am {prop.name} page. And my name is {nam}, and my full name is {nam2}</h1>
    <p>And my location is :{loc.pathname}</p>
    {loc.pathname===`/about/jawad/Ahmad`? <button>Hal Goda</button>:null}
    </>
  )
}
export default About;
