import React, { useState } from "react";
import "./index.css";
const App = () => {
  var [listval, listUpval] = useState("");
  var [arrval, arrUpval] = useState([]);
  var thisVal = (obj) => {
    const values = obj.target.value;
    listUpval(values);
  }

  const buttonToList = () => {
    arrUpval((prearr) => {
      return (
        [
          ...prearr, listval,
        ]
      )
    });
    listUpval('');
  };


  const deleteme = (id) => {
    arrUpval((prearr) => {
      return (prearr.filter((arr, i) => {
        return i !== id;
      })
      )
    })
  };

  return (
    <>
      <div className="center">
        <div className="cen">
          <h1 className="back">ToDo List</h1>
          <span>
            <input value={listval} type="text" placeholder="Enter list of Tasks" onChange={thisVal} />
            <button onClick={buttonToList}>+</button>
          </span>
          <ol>
            {arrval.map((val, index) => {
              return <li  ><a id={index}onClick={deleteme} className="size" href="#">*</a>{val}</li>
            })}
          </ol> </div>
      </div>
    </>
  )
}
export default App;

