import React, { useState } from "react";
import "./index.css";

const App = () => {
  var[pre, update]=useState('');
  let sent=(obj)=>{
    obj.preventDefault();
  }
  var thisPut=(obj1)=>{
     var name=obj1.target.name;
     var value=obj1.target.value;
     update(value)
  }
  return (
    <>
      <form onSubmit={sent}>
        <div className="center">
          <div className="cen">
            <h1>Hello {pre.no}</h1>
            <input type="number" name='no' onChange={thisPut} />
            <button type='submit'>Submit</button>
          </div>
        </div>
      </form>
    </>
  )
}
export default App;
