import React, { createContext, useEffect, useState } from 'react';
import axios from "axios";
const FirstN = createContext();
const LastN = createContext();


const App = () => {
  const[num,upNum]=useState(1);
  const[name,upName]=useState();
  const[Move,upMove]=useState()
  const curVal=(obj)=>{
   upNum(obj.target.value)
  };

  useEffect(()=>{
    async function  func(){
      const res=await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`)
      upName(res.data.name)
      console.log(res)
      upMove(res.data.Mo)
    }
    func();
  })

  return (
    <>
    <h1 >You select this <span style={{color:'red'}}> {num}</span> No</h1>
    <h1 >Name of this is <span style={{color:'red'}}> {name}</span> </h1>
    <h1 >And total move of {name} is <span style={{color:'red'}}> {Move}</span> </h1>

    <select onChange={curVal}>
      <option value="1"> 1 </option>
      <option value="25"> 25 </option>
      <option value="3"> 3 </option>
      <option value="4"> 4 </option> 
      <option value="5"> 5 </option>
    </select>
    </>
  )
}
export default App;
