import React, { useState } from "react";
import "./index.css";

const App=()=>{
    const [name, upName]=useState("Click Me");
 const [bg, upBg]=useState("red");
 const bgChange= ()=>{
     upBg('orange');
     upName('Cha')
 };
    return(
        <>
        <div style={{backgroundColor:bg}} className="center">
        <button onClick={bgChange} >{name}</button>
        </div>
        </>
    )
}
export default App;
