import React, { useState } from "react";
import "./index.css";
import Todo_list from "./Todo_List";

const App=()=>{
  var valo;
  const[arr,updArr]=useState([]);
  const[val,updVal]=useState('');
const thisVal=(obj)=>{
  updVal(obj.target.value);
}
const listMake=()=>{
  updArr((prearr)=>{
    return(
      [...prearr, val]
    )
  })
};
return(
  <>
      <div>
        <div className="center">
          <div className="cen">
            <h1 className="back">ToDo List ♥</h1>
            <span>
              <input type="text" placeholder="Enter value" onChange={thisVal} />
              <button onClick={listMake} >+</button>
            </span>
            <ol>
            {a}
             <li>{arr}</li>
            </ol> </div>
        </div>


      </div>
    </>
)
}
