import React, { useState } from "react";
import "./index.css";

const App=()=>{

    const [Name, upName]=useState('');
    const submit=()=>{
        upName(tt.target.value);
    }
    const call=(obj)=>{
       Name=obj.ta
    }
    return(
        <>
        <div className="center">
        <div className="cen">
            <h1>Hello {Name}</h1>
            <input type="text"  placeHolder="Type Your Name..." onChange={call}/>
            <button onClick={submit}>Submit</button>
            </div>
        </div>
        </>
    )
}
export default App;
