import React, { useState } from "react";
import "./index.css";

const App=()=>{
    var bg="purple";
 const [bgo, upBg]=useState(bg);
 const bgChange= ()=>{
     upBg('red');
 }
    return(
        <>
        <div style={{backgroundColor:bg}} className="center">
        <button onClick={bgChange} >Click Me</button>
        </div>
        </>
    )
}
export default App;
