import React, { useState } from "react";
import "./index.css";

const App=()=>{
    let [Name,Update]=useState('');
    let message=(obj)=>{
       Name=obj.target.value;
       Update(Name);
    }
    
    return(
        <>
        <div className="center">
        <div className="cen">
            <h1>Hello {Name}</h1>
            <input type="text" placeholder="Enter your Name." onChange={message} />
            <button >Submit</button>
            </div>
        </div>
        </>
    )
}
export default App;
