import React, { useState } from "react";
import "./index.css";
import Todo_list from "./Todo_List";
const App = () => {
  var [listval, listUpval] = useState("");
  var [arrval, arrUpval] = useState([]);
  var thisVal = (obj) => {
    const values = obj.target.value;
    listUpval(values);
  }

  const buttonToList = () => {
    arrUpval((prearr) => {
      return (
        [
          ...prearr, listval,
        ]
      )
    });
    listUpval('');
  };


  const deleteme = (id) => {
    console.log("deleted");
    arrUpval((prearr) => {
      return (prearr.filter((arr, i) => {
        return i !== id;
      })
      )
    })
  };


  return (
    <>
      <div>
        <div className="center">
          <div className="cen">
            <h1 className="back">ToDo List üòç‚öî</h1>
            <span>
              <input value={listval} type="text" placeholder="Enter list of Tasks" onChange={thisVal} />
              <button onClick={buttonToList}>+</button>
            </span>
            <ol>
              {arrval.map((val, index) => {
                return (<li className="bas" ><a id={index} key={index} onClick={() => {
                 return deleteme(index);
                }} className="size" href="#">üñêÔ∏è</a> {val}</li>
              )})}
            </ol> </div>
        </div>
      </div>
    </>
  )
}
export default App;

