import React, { useState } from "react";
import "./index.css";

const App=()=>{
    let [fullName,UpdFullName]=useState({
        fname:"",
        lname:"",
    });

    let message=(obj)=>{
       const value=obj.target.value;
       const name=obj.target.name;

       UpdFullName((preval)=>{
           if(name==='fname'){
              return( fname=value,
                      lname=preval.lname)
           }else if(name==='lname'){
               return (fname=preval.fname,
               lname=value)
           }
       })
    }

const send=(event)=>{
    event.preventDefault();
   
}
    return(
        <>
        <form onSubmit={send}>
        <div className="center">
        <div className="cen">
            <h1>Hello {fullName.fname} {fullName.lname} </h1>

            <input type="text" placeholder="Enter your Name."
             onChange={message} 
                 name="fname"
                 value
              />

            <input type="text" placeholder="Enter your Name."
             onChange={message} 
                 name='lname'
             />

            <button type="submit"  >Submit</button>
            </div>
        </div>
        </form>
        </>
    )
}
export default App;
