import React, { useState } from "react";
import "./index.css";

const App=()=>{
    
    const[time, curtime]=useState(new Date().toLocaleTimeString());
    const currtime=()=>{
        curtime(new Date().toLocaleTimeString())
    };
    setInterval(currtime,1000);
    return(
        <>
        <div className="center">
        <h1>{time}</h1>
        </div>
        </>
    )
}
export default App;
