import React, { useState } from "react";
import "./index.css";

const App=()=>{
    let [Name,Update]=useState('');
    let message=(obj)=>{
       Name=obj.target.value;
    }
const send=()=>{
    Update(Name);
}
    return(
        <>
        <form onSubmit>
        <div className="center">
        <div className="cen">
            <h1>Hello {Name}</h1>
            <input type="text" placeholder="Enter your Name." onChange={message} />
            <button type="submit" onClick={send} >Submit</button>
            </div>
        </div>
        </form>
        </>
    )
}
export default App;
