import React, { useState } from "react";
import "./index.css";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = () => {

 const[title, upTitle]=useState('');
const inpo=(obj)=>{
    upTitle(obj.target.value)
}

    return (
        <>
            <div class="mx-auto text-center mt-3" style={{ width: '200px' }}>
                <form className="text-center ">
                    <div class="card shadow-lg " style={{ width: "23rem" }}>
                        <input value="title" onChange={inpo} type="text"  placeholder="Title                                 📷  📝 💬" />
                        <div class="card-body">
                            <textarea className="w-100 m-0 h-100" placeholder="Enter Text..." rows="" column="" />
                            <Button  className="bton">
                                <AddIcon  />
                            </Button>
                        </div>
                    </div>
                </form>
            </div>


        </>
    )
}
export default CreateNote;

